catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}

	var gr = new GlideRecord('x_cph_sec_2202');
	gr.addQuery('sys_id',newValue);
	gr.query(function(gr) {    
		if(gr.next())   
		g_form.setValue('rek_name',gr.getValue('name'));
		g_form.setValue('rek_company',gr.getValue('company'));
		g_form.setValue('rek_date',gr.getValue('sys_created_on'));

		if(gr.getValue('udlaant') == 'true'){
			g_form.setValue('udlaant','true');
		}
		else{
			g_form.setValue('udlaant','false');
		}

		if(gr.getValue('modtaget') == 'true'){
			g_form.setValue('modtaget','true');
		}



		if(gr.getValue('udlaant_af')){
			g_form.setValue('udlaant_af',gr.getValue('udlaant_af'));
		}



		if(gr.getValue('udlaan_date_time')){
			g_form.setValue('udlaan_date_time',gr.getValue('udlaan_date_time'));
		}


			if(gr.getValue('udlaanssted')){
			g_form.setValue('udlaanssted',gr.getValue('udlaanssted'));
		}


			if(gr.getValue('vilkaar_bagageomraade')){
			g_form.setValue('vilkaar_bagageomraade',gr.getValue('vilkaar_bagageomraade'));
		}


		if(gr.getValue('vilkaar_crew')){
			g_form.setValue('vilkaar_crew',gr.getValue('vilkaar_crew'));
		}


		if(gr.getValue('id_card_type')){
			g_form.setValue('id_card_type',gr.getValue('id_card_type'));
		}


		if(gr.getValue('id_card_number')){
			g_form.setValue('id_card_number',gr.getValue('id_card_number'));
		}

		if(gr.getValue('zonekortnr')){
			g_form.setValue('zonekortnr',gr.getValue('zonekortnr'));
		}

		if(gr.getValue('remarks')){
			g_form.setValue('remarks',gr.getValue('remarks'));
		}


	});  

}
