catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}

	var pattern =/^[0-9]*$/;

	if((newValue.length > 4 || newValue.length < 4) || pattern.test(newValue) == false) {
		g_form.hideFieldMsg('handler_gate', true);
		g_form.setValue('handler_gate', oldValue);
		g_form.showFieldMsg('handler_gate', 'Indast klokkeslæt tal i følgende format', 'error');
		g_form.showFieldMsg('handler_gate', 'Eksempel 1: 0630', 'error');
		g_form.showFieldMsg('handler_gate', 'Eksempel 2: 1417', 'error');
		g_form.showFieldMsg('handler_gate', 'Eksempel 3: 2359', 'error');
		top.window.document.getElementById('sp_formfield_IO:040c700b0f921b00c4ccc9cce1050e19').focus();
		return;
	} else {
		g_form.hideFieldMsg('handler_gate', true);
	}

	var hours = parseInt(newValue.substring(0,2));
	var minutes = parseInt(newValue.substring(2,4));

	if((hours < 0 || hours > 23) || (minutes < 0 || minutes > 59)) {
		g_form.hideFieldMsg('handler_gate', true);
		g_form.setValue('handler_gate', oldValue);
		g_form.showFieldMsg('handler_gate', 'Indast klokkeslæt tal i følgende format', 'error');
		g_form.showFieldMsg('handler_gate', 'Eksempel 1: 0630', 'error');
		g_form.showFieldMsg('handler_gate', 'Eksempel 2: 1417', 'error');
		g_form.showFieldMsg('handler_gate', 'Eksempel 3: 2359', 'error');
		top.window.document.getElementById('sp_formfield_IO:040c700b0f921b00c4ccc9cce1050e19').focus();
	}

}
