sys_script_include.script:
var bnowupdaterecord_sec2202 = Class.create();
bnowupdaterecord_sec2202.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	updateRecord: function() {

		var sysID = this.getParameter('sysparm_sys_id');
		var udlaantValue = this.getParameter('sysparm_udlaant');



		var gr = new GlideRecord('x_cph_sec_2202');
		gr.addQuery('sys_id',sysID);
		gr.query();

		if(gr.next()){

			if(udlaantValue == 'true'){

				gr.modtaget = 'true';
				gr.modtaget_af = this.getParameter('sysparm_modtaget_af');
				gr.modtaget_date_time = this.getParameter('sysparm_modtaget_date_time');

			}
			else{
				gr.udlaant = 'true';
				gr.udlaant_af = this.getParameter('sysparm_udlaant_af');
				gr.udlaan_date_time = this.getParameter('sysparm_udlaan_date_time');
				gr.udlaanssted = this.getParameter('sysparm_udlaanssted');
				gr.vilkaar_bagageomraade = this.getParameter('sysparm_vilkaar_bagageomraade');
				gr.vilkaar_crew = this.getParameter('sysparm_vilkaar_crew');
				gr.id_card_type = this.getParameter('sysparm_id_card_type');
				gr.id_card_number = this.getParameter('sysparm_id_card_number');
				gr.zonekortnr = this.getParameter('sysparm_zonekortnr');
				gr.remarks = this.getParameter('sysparm_remarks');

			}
			gr.update();
		}
	}
});
